<section id="section-form" class="section-form section">
    <div class="container">
        <h2>Write to us</h2>
        <form class="form__contact-form" id="form">
            <div class="form__field">
                <input type="text" name="name" required maxlength="30">
                <label>Name</label>
                <span class="counter" data-counter="30">30</span>
            </div>
            <div class="form__field">
                <input type="text" name="email" id="email" required maxlength="50">
                <label>E-mail</label>
                <span class="counter" data-counter="50">50</span>
            </div>
            <div class="form__field">
                <input type="text" name="message" required maxlength="100">
                <label>Message</label>
                <span class="counter" data-counter="100">100</span>
            </div>
            <button><i class="far fa-comment-dots"></i></button>
            <div class="loading">
