                    <div class="preloader__element"></div>
                    <div class="preloader__element"></div>
                    <div class="preloader__element"></div>
                    <div class="preloader__element"></div>
                    <div class="preloader__element"></div>
                </div>
            </div>
        </form>
        <div class="message"></div>
    </div>
</section>
